import React, { Fragment, useState } from "react";
import Header from "./components/Header";
import Lista from "./components/Lista.js";
import Todolist from "./components/ToDoList";

function App() {
  const [suggestionslist, setsugestionslist] = useState([]);
  const addsugestionhandler =(sugest)=>{
      isetsugestions((prevlistsugestion)=>{
      return[...prevlistsugestion, {sugest: suggestionslist, id:Math.random().toString()},];
    });
  };

  return (
    <Fragment>
      <Header/>
      <div>
      <Todolist onToDolist={addsugestionhandler}/>
      </div>
      <Lista users={suggestionslist}/>
    </Fragment>
  );
}
export default App;
