import React, { Fragment, useState } from "react";
import Header from "./components/Header";
import Lista from "./components/Lista.js";
import Todolist from "./components/ToDoList";

function App() {
  const [suggestions, setsugestions] = useState([]);

  const addsugestionhandler =(sugest)=>{
      setsugestions((prevlistsugestion)=>{
      return[...prevlistsugestion, {sugest: suggestions, id:Math.random().toString()}]
    });
  };

  return (
    <Fragment>
      <Header/>
      <div>
      <Todolist onToDolist={addsugestionhandler}/>
      </div> 
      <div>
      <Lista users={suggestions}/>
      </div>

    </Fragment>
  );
}
export default App;
