import React, { useState } from 'react'; 
import ListPeople from '../components/ListPeople';
import classes from '../components/ListPeople.module.css'
import classes1 from '../components/Card.module.css' 
import Card from './Card.js';
import Button from './Button';

const ListPeopleHandler = (props) => {
  const [people, setPeople] = useState([]);
  async function fetchPeopleHandler() {
     const response = await fetch('https://jsonplaceholder.typicode.com/users');
       const data = await response.json();
         console.log(data);
           const transformedPeople = data.map(PeopleData => {
             return {
               id: PeopleData.id,
               name: PeopleData.name,
               username: PeopleData.username,
               email: PeopleData.email,
               };
           });
           setPeople(transformedPeople); 
     };
  return (
    <Card className={classes.users}>
    <ul>
      <li className={classes['users ul']}>
        {props.peoples.map((people) => (
           <ListPeople 
           key={people.id}
           name={people.name}
           username = {people.username}
           email = {people.email}
           />
        ))}
      </li>
      </ul>
      <Button onClick={fetchPeopleHandler}>IMPORT wwww PEOPLE</Button>
      </Card>
  );
};

export default ListPeopleHandler;
